(function($){$.fn.gephiSvgInteractive=function(options){return this.each(function(){var optionsDefault={cursor:"pointer"};Object.keys(optionsDefault).forEach(function(option){if(!options[option]){options.option=optionsDefault[option]}});options.class?$(this).addClass(options.class):null;options.cursor?$(this).find("#edges path, #nodes circle, #node-labels text").css("cursor",options.cursor):null;$(this).bind("click.gephiSvgInteractive",function(){if(event.target!==this){var target=$(event.target);if(target.is("#edges path")){target.css("opacity","1");target.siblings().each(function(){$(this).css("opacity","0.1")})}else if(target.is("#node-labels text")||target.is("#nodes circle")){var nodeId=target.attr("class");target.parents("svg").find("#edges path."+nodeId).css("opacity","1");target.parents("svg").find("#edges path").not("."+nodeId).css("opacity","0.1")}}else{$(this).find("path").css("opacity","1")}})})}})(jQuery);